import fetch from 'isomorphic-unfetch'
import Layout from '../../components/Layout'

const Pokemon = props => {
    return (
        <Layout pageTitle={props.data.name}>
            <div className="columns content-column">
                <div className="column">
                    <h1 className="is-size-1 has-text-centered">{props.data.name.uppercase()}</h1>
                    <ul>
                        { props.data.types.map(type => {
                            return (
                                <li key={type.slot}>{type.name}</li>
                            )
                        })}
                    </ul>
                </div>
            </div>

            <style jsx>{`
                .content-column {
                    margin-top: 124px;
                }
            `}</style>
        </Layout>
    )
}

Pokemon.getInitialProps = async function (context) {
    const {id} = context.query

    const res = await fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)
    const data = await res.json()

    console.log(`Fetched pokemon: ${data.name}`)

    return { data }
}

export default Pokemon
