import fetch from 'isomorphic-unfetch'
import useSWR from 'swr'

import Layout from "../components/Layout"
import PokeCard from '../components/PokeCard'

function fetcher(url) {
    return fetch(url).then(uData => uData.json())
}

const IndexPage = props => {
    return (
        <Layout pageTitle="Home">
            <div className="columns is-multiline content-column">
                { props.pokemon.results.map(pk => {
                    return (
                        <div className="column is-one-third" key={pk.name}>
                            <PokeCard
                                id={pk.name}
                                name={pk.name}
                            />
                        </div>
                    )
                }) }
            </div>

            <button onClick={ () => Router.push(`${props.pokemon.next}`) }>Next</button>

            <style jsx>{`
                .content-column {
                    margin-top: 124px;
                }
            `}</style>
        </Layout>
    )
}

IndexPage.getInitialProps = async function ({
    query: { limit = 30, offset = 0 }
}) {
    const res = await fetch(`https://pokeapi.co/api/v2/pokemon?offset=${offset}&limit=${limit}`)
    const data = await res.json()

    console.log(`Fetched pokemon: ${data.results.length}`)

    return { 
        pokemon: data,
        limit: parseInt(limit, 30),
        offset
    }
}

export default IndexPage
